import { useDispatch, useSelector } from 'react-redux';
import { counterActions, CounterState } from '../store';
import styles from './Counter.module.css';

const Counter = () => {
  const counter = useSelector((state: CounterState) => state.counter);
  const dispatch = useDispatch();

  const show = useSelector((state: CounterState) => state.showCounter);

  const incrementHandler = () => {
    dispatch({ type: 'increment' });
  };

  const increaseHandler = () => {
    dispatch({ type: 'increase', amount: 5 });
  };

  const decrementHandler = () => {
    dispatch({ type: 'decrement' });
  };

  const toggleCounterHandler = () => {
    dispatch({ type: 'toggleCounter' });
  };




  return (
    <main className={styles.counter}>
      <h1>Redux Counter</h1>

      {show && <div className={styles.counter__value}>{counter}</div>}
      <div>
        <button onClick={incrementHandler}>Increment</button>
        <button onClick={decrementHandler}>Decrement</button>
        <button onClick={increaseHandler}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
