    counter: 'counter',
    value: 'counter__value',
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}0</div>
      <div>
        <button onClick={() => dispatch({ type: 'increment' })}>Increment</button>
